<?phpsession_start();include_once('connect.php');include_once('functions.php');$error_otp = '';$user_activation_code = '';$name = '';$password = '';$email = '';$message = '';if(isset($_GET['msg'])){  otp_check();}if(isset($_SESSION['code'])){  $user_activation_code =  $_SESSION['code'];  $name =  $_SESSION['name'];  $password =  $_SESSION['password'];  $email =  $_SESSION['email'];  if(isset($_POST['submit']))  {    if(empty($_POST['otp']))    {      $error_otp = '<label class="text-danger">Enter OTP</label>';      blank();    }    else    {      $otp = trim($_POST['otp']);    }    if($error_otp == '' )    {      $info ="select user_otp from register_user where user_activation_code = '$user_activation_code' and user_otp = '$otp'" ;      $result=mysqli_query($conn,$info);      $row = mysqli_fetch_array($result);      $check_otp = $row['user_otp'];      if ($check_otp == $otp)      {        $user_id = md5(rand());        $status = "not verified";        $sql5 = "insert into verfied_user(name,password,email,user_id,status) values                                   ('$name','$password','$email','$user_id','$status')";        if ($conn->query($sql5))        {          $msg = "Registraion Complete";          header('location:login.php?msg='.$msg);        }        else{          error_reg();        }      }      else      {        otp_error();      }    }  }}?><html><head><title>OTP_Checking</title><link rel="stylesheet" href="design.css"></head><body> <div class="otp-page"> <img src="download (1).png" class="avatar"> <div class="form">     <form action="#" method="post" >   <h2>Enter OTP</h2>   <input type="password" name="otp" placeholder="OTP">   <button type="submit" name="submit" class ="btn btn-primary">Enter</button>      </form> </div> </div></body></html>